    /// </summary>
	[ModuleMapping(ModuleTypes.Modifier, "buffer")]
    public class Cache : ModuleBase
    {
        #region Fields

        #endregion

        #region Constructors

        /// <summary>
        /// Initializes a new instance of Cache.
        /// </summary>
        public Cache()
            : base(1)
